mapper.select:
select to_char(p_tjtime,'yyyy-mm-dd HH24:mi:ss') p_tjtime, to_char(p_time,'yyyy-mm') p_time, P_ID,U_ID,P_BRAND
,P_SERIES,P_AGE,P_PRICE,P_MIAOSHU,P_STATE

 from personneed where 1=1 

and u_id=#{uId}


and p_brand=#{pBrand}


and p_age=#{pAge}


and p_series=#{pSeries}


and p_miaoshu=#{pMiaoshu}


and to_char(p_time,'yyyy-mm')=#{pTime}


and p_price=#{pPrice}


and p_state=#{pState}

select * from(select rownum rn , b.* from(select to_char(a.p_tjtime,'yyyy-mm-dd HH24:mi:ss') p_tjtime, to_char(a.p_time,'yyyy-mm') p_time, a.P_ID,a.U_ID,a.P_BRAND ,a.P_SERIES,a.P_AGE,a.P_PRICE,a.P_MIAOSHU,a.P_STATE from personneed a where 1=1
and u_id=#{uId}
order by p_tjtime desc ) b where rownum<=(#{curPage})*(#{rowsPrePage})) where rn>((#{curPage})-1)*(#{rowsPrePage})

mapper.delete:
delete from personneed where 1=1 

and p_id=#{pId,jdbcType=DECIMAL}


and u_id=#{uId,jdbcType=DECIMAL}
